import React, { useEffect, useState } from "react";
import "./css/style.css";
const Weather = () => {
  const [searchinfo, setsearchinfo] = useState("delhi");
  const [temp,settemp]=useState("");
  const changeInfo = async () => {
    try {
      let url = `https://api.openweathermap.org/data/2.5/weather?q=${searchinfo}&units=metric&appid=d15c1d923d610ce3c1fea8b5502d36c7`;

      let res = await fetch(url);
      let data = await res.json();
      const { temp,humidity,pressure }=data.main;
      const {main:weathermood}=data.weather[0];
      const {name}=data;
      const {speed}=data.wind;
      const {country,sunset}=data.sys; 

      const mynewweatherInfo={
        temp,
        humidity,
        pressure,
        weathermood,
        name,
        speed,
        country,
        sunset,
      };
      settemp(mynewweatherInfo);
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    changeInfo();
  }, []);
  return (
    <>
      <div className="box">
        <div className="box-info">
          <input
            type="search"
            placeholder="search.."
            className="inp"
            value={searchinfo}
            onChange={(e) => {
              setsearchinfo(e.target.value);
            }}
          ></input>
          <button
            type="text"
            placeholder="search"
            className="btn"
            onClick={changeInfo}
          >
            Search
          </button>
        </div>
      </div>
      <article className="arc">
        <div className="inside-arc">
          <i className={"wi wi-day-sunny first-icon"}></i>
        </div>
        <div className="Temp">
          28.57
          <div className="status">
            Clouds
            <p className="para">Tokyo,jp</p>
          </div>
          <div className="date">{new Date().toLocaleString()}</div>
        </div>
        <div className="last-sec">
          <div className="icon">
            <i className="wi wi-day-sunny"></i>
            <div className="section">
              19:19 Pm
              <p>sunset</p>
            </div>
          </div>
          <div className="icon-2">
            <i className="wi wi-rain"></i>
            <div className="section">
              19:19 Pm
              <p>Rainy</p>
            </div>
          </div>
          <div className="icon-3">
            <i className="wi wi-humidity"></i>
            <div className="section">
              19:19 Pm
              <p>Humidity</p>
            </div>
          </div>

          <div className="icon-4">
            <i className="wi wi-strong-wind"></i>
            <div className="section">
              19:19 Pm
              <p>wind</p>
            </div>
          </div>
        </div>
      </article>
    </>
  );
};
export default Weather;

